// Parse Claude's response and extract the idea JSON
const response = $input.first().json;
const content = response.content[0].text;

// Try to parse the JSON from Claude's response
let idea;
try {
  // Handle if Claude wrapped it in markdown code blocks
  const jsonMatch = content.match(/```json?\n?([\s\S]*?)\n?```/) || [null, content];
  idea = JSON.parse(jsonMatch[1] || content);
} catch (e) {
  idea = {
    idea: content,
    category: 'uncategorized',
    priority: 'someday',
    related_to: null,
    raw_input: $('Idea Webhook').first().json.body.message,
    captured_at: new Date().toISOString(),
    parse_error: true
  };
}

// Add metadata
idea.id = `idea_${Date.now()}`;
idea.source = $('Idea Webhook').first().json.body.source || 'cli';
idea.user = $('Idea Webhook').first().json.body.user || 'will';

return [{ json: idea }];
